package com.google.gcloud.com.google.gcloud.spi;import com.google.common.collect.Iterables;import com.google.gcloud.datastore.DatastoreServiceOptions;import com.google.gcloud.storage.StorageServiceOptions;import java.util.ServiceLoader;public class ServiceRpcProvider {  public static DatastoreRpc datastore(DatastoreServiceOptions options) {    DatastoreRpcFactory factory =        Iterables.getFirst(ServiceLoader.load(DatastoreRpcFactory.class), null);    return factory == null ? new DefaultDatastoreRpc(options) : factory.create(options);  }  public static StorageRpc storage(StorageServiceOptions options) {    StorageRpcFactory factory =        Iterables.getFirst(ServiceLoader.load(StorageRpcFactory.class), null);    return factory == null ? new DefaultStorageRpc(options) : factory.create(options);  }}